import {createSlice, createAsyncThunk} from '@reduxjs/toolkit'
import axios from 'axios'

const initialState = {
    loading: false,
    exercise: [],
    error: ''

}


export const addNewExercise = createAsyncThunk('exercise/createExercise', async (exerciseName, exerciseDescription, exerciseLevel) => {
    // const res = await axios.post('http://localhost:5003/api/createExercise', {
    //     exerciseName,
    //     exerciseDescription,
    //     exerciseLevel
    // })
    // .then(res => {

    //     console.log(res.data)
    //     return res.data.map(exercise => exercise)

    // })
    // .catch(err => {
    //     console.log(err.message)
    // })


})

const exerciseSliceReducer = createSlice({
    name: 'exercise',
    initialState,
    extraReducers: builder =>  {
        builder.addCase(addNewExercise.pending, state => {
            state.loading = true
        })
        builder.addCase(addNewExercise.fulfilled, (state, action) => {
            state.loading = false
            state.exercise = action.payload
        })
        builder.addCase(addNewExercise.rejected, (state, action) => {
            state.loading = false
            state.exercise = []
            state.error = action.error.message
        })
    }

})

export default exerciseSliceReducer.reducer
