import classes from '../assets/createExercise.module.css'
import React, { useState } from 'react'
import axios from 'axios'

const CreateExerciseForm = () => {
    const [exerciseName, setExerciseName] = useState('')
    const [exerciseDescription, setExerciseDescription] = useState('')
    const [difficulty, setDifficulty] = useState(0)


    const onSubmitHandler = (e) => {
      e.preventDefault()
      console.log(exerciseName,exerciseDescription,difficulty)
      axios.post('http://localhost:5003/api/createExercise', {
        exerciseName,
        exerciseDescription,
        difficulty
    })
    .then(res => {
        console.log(res.data)
    })
    .catch(err => {
        console.log(err.message)
    })
    }

  return (
    <div className={classes.Page}>
        <form onSubmit={onSubmitHandler}>
          <div className={classes.Container}>
            <h1 className={classes.Title}>Create Workout</h1>
            <div>

              <input type='text' value={exerciseName} onChange={(e) => setExerciseName(e.target.value)} placeholder="Name Of exercise"/>
            </div>
            <div>

              <input type='text' value={exerciseDescription} onChange={(e) => setExerciseDescription(e.target.value)} placeholder="Brief Description"/>
            </div>
            <div>

              <input type='text' value={difficulty} onChange={(e) => setDifficulty(e.target.value)}/>
            </div>
            <button type="submit">Submit</button>
            </div>
        </form>

    </div>
  )
}

export default CreateExerciseForm
