import {createSlice, createAsyncThunk} from '@reduxjs/toolkit'
import axios from 'axios'

const initialState = {
    loading: false,
    exercise: [],
    error: ''
}

export const createExercise = createAsyncThunk('exercise/createExercise', async (exerciseName, exerciseDescription, difficulty) => {
    const res = await axios.post('http://localhost:5003/api/createExercise', {
        exerciseName,
        exerciseDescription,
        difficulty
    })
    .then(res => {
        console.log(res.data.data)
        res.data.map(exercise => exercise)
    })
    .catch(err => {
        console.log(err.message)
    })

})

const exerciseSliceReducer = createSlice({
    name: 'exercise',
    initialState,
    reducers
})

export default exerciseSliceReducer.reducer
