import {createSlice, createAsyncThunk} from '@reduxjs/toolkit'
import axios from 'axios'

const initialState = {
    loading: false,
    exercise: [],
    error: ''
}

export const createExercise = createAsyncThunk('exercise/createExercise', () => {
    return axios.post('http://localhost:5003/api/createExercise', {
        exerciseName,
        exerciseDescription,
        difficulty
    })
    .then(res => res.data)

})

const exerciseSliceReducer = createSlice({
    name: 'exercise',
    initialState,
    extraReducers: builder => {
        builder.addCase(createExercise.pending, state => {
            state.loading = true
        })
        builder.addCase(createExercise.fulfilled, (state,action) => {
            state.loading = false
            state.exercise = action.payload
            state.error = ''
        })
        builder.addCase(createExercise.rejected, (state,action) => {
            state.loading = false
            state.exercise = []
            state.error = action.error.message
        })
    }
})

export default exerciseSliceReducer.reducer
