import React, { useState } from 'react'
import { useForm } from 'react-hook-form'
import { registerUser } from '../features/user/userAction'
import { useDispatch, useSelector } from 'react-redux'

const SignUpForm = () => {
    const [userName, setUserName] = useState("")
    const [email, setEmail] = useState('')
    const [password, setPassword] = useState('')

    const dispatch = useDispatch;
    const {register, handleSubmit} = useForm()


   const submitForm = (data) => {
    dispatch(registerUser(data))
   }


  return (
    <div>
        <form onSubmit={handleSubmit(submitForm)}>
            <input type="text" {...register('userName')}/>
            <input type="text" onChange={(e) => setEmail(e.target.value)} value={email} {...register('email')}/>
            <input type="text" onChange={(e) => setPassword(e.target.value)} value={password} {...register('password')}/>
            <button type="submit" >Sign Up</button>
        </form>

    </div>
  )
}

export default SignUpForm
