import axios from 'axios'
import React, { useState } from 'react'
import { useLocation } from 'react-router-dom'

const RecordWorkout = () => {
    const [workoutName, setWorkoutName] = useState('')
    const [reps, setReps] = useState(0)
    const [done, setDone] = useState(false)
    const [workout, setWorkout] = useState([])

    const location = useLocation()

    console.log(location.pathname.slice(5,40))

    const addWorkout = object => {
        setWorkout([
            ...workout,
            object

        ])
        setWorkoutName('')
        setReps(0)
    }

    const saveWorkout = e => {
        e.preventDefault();

        axios.put()
    }

    console.log(workout)

    const recordWorkout = e => {
        while(done === false) {
            return <div>
                <input type='text' onChange={e => setWorkoutName(e.target.value)} value={workoutName}/>
                <input type="number" onChange={e => setReps(e.target.value)}/>
                <button onClick={() => {
                    addWorkout({
                        workoutName: workoutName,
                        reps: reps
                    })
                }}>Add Exercise</button>
                <button onClick={() => {
                    setDone(true)
                }}>Record Exercise</button>
            </div>


        }
        return <h1>Workout Saved, check results here!</h1>
    }

  return (
    <div>
        {recordWorkout()}

    </div>
  )
}

export default RecordWorkout
