import axios from 'axios'
import React, { useState, useEffect } from 'react'

const InititateWorkoutPage = (props) => {
    const [time, setTime] = useState(0)
    const [timeOn, setTimeOn] = useState(false)

    const {id, setCompletionTime} = props;


    useEffect(() => {
        let interval = null;

        if (timeOn) {
            interval = setInterval(() => {
                setTime(prevTime => prevTime + 10)
            }, 10)
        } else {
            clearInterval(interval)
        }

        return () => clearInterval(interval)

    }, [timeOn])

    const finishWorkout = () => {
        axios.post(`http://localhost:5003/api/regimen/${id}/exerciseQueueUpdate`)
    }

  return (
    <div>
        <div>
            <span>{("0" + Math.floor((time / 60000) % 100)).slice(-2)}:</span>
            <span>{("0" + Math.floor((time / 1000) % 60)).slice(-2)}:</span>
            <span>{("0" + ((time / 10) % 100)).slice(-2)}</span>
        </div>
        <div>
        <button onClick={() => {
            setTimeOn(true)
        }}>Start</button>
        <button onClick={() => {
            setTimeOn(false)
        }}>Stop</button>
        <button onClick={() => {
            setTimeOn(true)
        }}>Resume</button>
        <button onClick={() => {
            setTime(0)
        }}>Reset</button>
        <button>Finish</button>
        </div>
    </div>
  )
}

export default InititateWorkoutPage
