import {createSlice, createAsyncThunk} from '@reduxjs/toolkit'
import axios from 'axios'

const initialState = {
    exercise: {},

}

export const createExercise = (exerciseName, exerciseDescription, difficulty) =>  {
    axios.post('http://localhost:5003/api/createExercise', {
        exerciseName,
        exerciseDescription,
        difficulty
    })
    .then(res => {
        console.log(res)
    })
    .catch(err => {
        console.log(err)
    })
}

const exerciseSliceReducer = createSlice({
    name: 'exercise',
    initialState,
    reducers: {
        create: (state) => {
            state.exercise 
        }
    }
})

export default exerciseSliceReducer.reducer
