import {createSlice, createAsyncThunk} from '@reduxjs/toolkit'
import axios from 'axios'

const initialState = {
    loading: false,
    exercise: [],
    err: ''

}


const addNewExercise = createAsyncThunk('exercise/createExercise', async (exerciseName, exerciseDescription, exerciseLevel) => {
    axios.post('http://localhost3003/api/createExercise', {
        exerciseName,
        exerciseDescription,
        exerciseLevel
    })
    .then(res => {
        return res.data.data
    })
    .catch(err => {
        console.log(err.message)
    })
})

const exerciseSliceReducer = createSlice({
    name: 'exercise',
    initialState,
    extraReducers: builder =>  {
        builder.addCase(add.pending, state => {
            state.loading = true
        })
        builder.addCase(addNewPost.fulfilled, (state, action) => {
            state.loading = false
            state.exercise = action.payload
        })
        builder.addCase(addNewPost.rejected, (state, action) => {
            state.loading = false
            state.exercise = []
            state.error = action.error.message
        })
    }

})

export default exerciseSliceReducer.reducer
