import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import axios from 'axios'

const initialState = {
    loading: false,
    user: [],
    error: ''
}

export const login = createAsyncThunk('login/loginUser', async (email, password) => {
    const res = await axios.post('http://localhost:5003/login', {
        email,
        password
    })
        .then(res => res.data.map(usr => user))
        .catch(err => console.log(err))

    return res
})

const loginSliceReducer = createSlice({
    name: "login",
    initialState,
    extraReducers: builder => {
        builder.addCase(login.pending, state => {
            state.loading = true
        })
        builder.addCase(login.fulfilled, (state, action) => {
            state.loading = false
            state.user = action.payload
        })
        builder.addCase(login.rejected, (state, action) => {
            state.loading = false
            state.user = []
            state.error = action.error.message
        })
    }
})

export default loginSliceReducer.reducer
