import React, { useEffect, useState } from 'react'

const RecordWorkout = () => {
    const [workoutName, setWorkoutName] = useState('')
    const [reps, setReps] = useState(0)
    const [done, setDone] = useState(false)
    const [workout, setWorkout] = useState([])

    const addWorkout = object => {
        setWorkout([...object])
        setWorkoutName('')
        setReps(0)
    }

    console.log(workout)

    const recordWorkout = e => {
        while(done === false) {
            return <div>
                <input type='text' onChange={e => setWorkoutName(e.target.value)} value={workoutName}>Input Workout</input>
                <input type="number" onChange={e => setReps(e.target.value)}>Input reps</input>
                <button onClick={() => {
                    addWorkout({
                        workoutName: workoutName,
                        reps: reps
                    })
                }}>Add Exercise</button>
            </div>


        }
    }

  return (
    <div>
        {}

    </div>
  )
}

export default RecordWorkout
