import React, { useState } from 'react'
import { useForm } from 'react-hook-form'
import { registerUser } from '../features/user/userAction'
import { useDispatch, useSelector } from 'react-redux'
import {register, reset} from '../features/auth/authSlice'

const SignUpForm = () => {
    const [formData, setFormData] = useState({
        userName: "",
        email: "",
        password: ""
    })

    const {userName, email, password} = formData

    const dispatch = useDispatch;

    const {user, isLoading, isError, isSuccess, message} = useSelector((state) => state.auth)

    const onChange = (e) => {
        setFormData((prevState) => ({
            ...prevState,
            [e.target.name]: e.target.value,
        }))
    }


    // const submitForm = (data) => {

    //     // transform email string to lowercase to avoid case sensitivity issues during login
    //     data.email = data.email.toLowerCase()
    //     dispatch(registerUser(data))
    // }

    const onSubmitHandler = (e) => {
        e.preventDefault()

        const userData = {
            userName,
            email,
            password
        }
        dispatch(register())
    }

    return (
        <div>
            <form onSubmit={onSubmitHandler}>
                <input type="text" onChange={onChange} name="userName" value={userName}/>
                <input type="email" onChange={onChange} name="email"value={email}/>
                <input type="password" onChange={onChange} name="password" value={password}/>
                <button type="submit" >Sign Up</button>
            </form>

        </div>
    )
}

export default SignUpForm
