import { createSlice, createAsyncThunk, isRejectedWithValue } from "@reduxjs/toolkit";
import axios from 'axios'

const userToken = localStorage.getItem('userToken') ? localStorage.getItem('userToken') : null

const initialState = {
    loading: false,
    userInfo: null,
    userToken,
    error: null,
    success: false
}


export const registerUser = createAsyncThunk('user/register', async ({ userName, email, password }, { rejectWithValue }) => {
    try {
        const config = {
            headers: {
                'Content-Type': 'application/json'
            }
        }
        axios.post('http://localhost:5003/login', {
            userName,
            email,
            password
        })
    } catch (error) {

    }
})

const userSlice = createSlice({
    name: 'user',
    initialState,
    reducers: {},
    extraReducers: {
        [registerUser.pending]: (state) => {
            state.loading = true
            state.error = null
        },
        [registerUser.fulfilled]: (state, {payload}) => {
            state.loading = false
        }
    }
})

export const login = createAsyncThunk('login/loginUser', async ({ email, password }, { rejectWithValue }) => {
    try {
        const config = {
            headers: {
                'Content-Type': 'application/json'
            },
        }
        const { data } = await axios.post('http://localhost:5003/login', {
            email,
            password
        }, config)

        localStorage.setItem('userToken', data.userToken)
        return data
    } catch (error) {
        if (error.response && error.response.data.message) {
            return rejectWithValue(error.response.data.message)
        } else {
            return rejectWithValue(error.message)
        }
    }
})

