import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

// Get user from local storage

const user = JSON.parse(localStorage.getItem('user'))

const initialState = {
    user: user ? user : null,
    isError: false,
    isSuccess: false,
    isLoading: false,
    message: ""
}

export const authSlice = createSlice({
    name: 'auth',
    initialState,
    // reducers are for functions that are not asynchronous/asyncthunk 
    reducers: {
        reset: (state) => {
            state.isLoading = false,
            state.isError = false,
            state.isSuccess = false,
            state.isLoading = false,
            state.message = ""
        }
    },
    // extraReducers are for asynchronous thunk functions
    extraReducers: () => {

    }
})

export default authSlice.reducer
export const {reset} = authSlice.reducer
