import {createSlice, createAsyncThunk} from '@reduxjs/toolkit'
import axios from 'axios'

const initialState = {
    exercise: {},

}



const exerciseSliceReducer = createSlice({
    name: 'exercise',
    initialState,
    reducers: {
        create: (state) => {
            createExercise = (exerciseName, exerciseDescription, difficulty) =>  {
                axios.post('http://localhost:5003/api/createExercise', {
                    exerciseName,
                    exerciseDescription,
                    difficulty
                })
                .then(res => {
                    state.exercise = res.data
                })
                .catch(err => {
                    console.log(err)
                })
            }
        }
    }
})

export default exerciseSliceReducer.reducer
