import {createSlice, createAsyncThunk} from '@reduxjs/toolkit'
import axios from 'axios'

const initialState = {
    exercise: {},

}


const addNewPost = createAsyncThunk('exercise/createExercise', async (exerciseName, exerciseDescription, exerciseLevel) => {
    axios.post('http://localhost3003/api/createExercise', {
        exerciseName,
        exerciseDescription,
        exerciseLevel
    })
    .then(res => {
        return res.data.data
    })
    .catch()
})

const exerciseSliceReducer = createSlice({
    name: 'exercise',
