import React, {useState} from 'react'
import {useSelector, useDispatch} from 'react-redux'
import { createExercise } from '../features/CreateExerciseSlice'

const CreateExerciseForm = () => {
    const [exerciseName, setExerciseName] = useState('')
    const [exerciseDescription, setExerciseDescription] = useState('')
    const [difficulty, setDifficulty] = useState(0)

    const dispatch = useDispatch()
    const exercise = useSelector(state => state.createexercise)


  return (
    <div>
        <form onSubmit={createExercise}>
            <h1>Create Workout</h1>
            <input type='text' onChange={(e) => e.target.value} value={exerciseName}>Name of Exercise</input>
            <input type='text' onChange={(e) => e.target.value} value={exerciseDescription}> Exercise Description</input>
            <input type='number' onChange={(e) => e.target.value} value={difficulty}>Exercise Difficulty </input>
            <button type="submit">Create Exercise</button>
        </form>

    </div>
  )
}

export default CreateExerciseForm
